CREATE PROCEDURE procedure_name
  ASsql_statement
  GO;

Example

  CREATE PROCEDURE SelectAllCustomers 
  @City nvarchar(30)ASSELECT * FROM Customers WHERE 
  City = @CityGO;

Example

  CREATE PROCEDURE SelectAllCustomers 
  @City nvarchar(30), @PostalCode nvarchar(10)ASSELECT * FROM Customers WHERE 
  City = @City AND PostalCode = @PostalCodeGO;
