content.text:
.templates {
  display: flex;
  align-items: center;
  justify-content: center;
  flex-flow: row wrap;
}

.templates-item {
  margin: 10px;
  font-size: 30px;
}

if (window.addEventListener) {              
  window.addEventListener("load", showFrameSize);
  window.addEventListener("resize", showFrameSize);
} else if (window.attachEvent) {                 
  window.attachEvent("onresize", showFrameSize);
}

function showFrameSize() {
  var t;
  var width, height;
  width = Number(w3_getStyleValue(document.getElementById("editorEdit"), "width").replace("px", "")).toFixed();
  height = Number(w3_getStyleValue(document.getElementById("editorEdit"), "height").replace("px", "")).toFixed();
  document.getElementById("framesize").innerHTML = "Size: <span>" + width + " x " + height + "</span>";
}
