content.text:
@supports (property: value) {
  /* CSS rules */
}

codes.code:
/* use this CSS if the browser does not support display: flex */    
.container {
  float: left;
  width: 100%;
}
/* use this CSS if the browser supports display: flex */
@supports (display: flex) {
  .container {
    display: flex;
  }
}

/* use this CSS if the browser does not support display: grid */.container 
    {  display: table;  width: 90%;  background-color: 
    #2196F3;  padding: 10px;}/* use this CSS if the browser 
    supports display: grid */@supports (display: grid) {  .container 
    {    display: grid;    grid: auto;    
    grid-gap: 10px;    background-color: #2196F3;    
    padding: 10px;  }}

@supports not (display: grid) {
  .warning {
    background-color: pink;
    padding: 10px;
    border: 1px solid red;
  }
}
