content.text:
.flex-container {
  display: flex;
  flex-wrap: nowrap;
  background-color: DodgerBlue;
}

.flex-container > div {
  background-color: #f1f1f1;
  color:#000;
  width: 50%;
  margin: 10px;
  text-align: center;
  line-height: 50px;
  font-size: 24px;
}

ul.navbarmenu {
    list-style-type: none;
    margin: 0;
    padding: 0;
    overflow: hidden;
    background-color: #333;
}

ul.navbarmenu li {
    float: left;
}

ul.navbarmenu li a {
    display: inline-block;
    color: white;
    text-align: center;
    padding: 14px 16px;
    text-decoration: none;
}

ul.navbarmenu li a:hover {
    background-color: #111;
}

ul.navbarmenu li a.active {
    background-color: red;
}

