var storageBlip = "js_";
var exnames = [];
var answerObj = {};

if (window.addEventListener) {
  window.addEventListener("load", w3sOnLoad);
} else if (window.attachEvent) {
  window.attachEvent("onload", w3sOnLoad);
}
function w3sOnLoad() {
  UserSession.processUserSession({
    context: 'Exercise QA',
    handleRedirection: true,
    callback: function () {
      userLoggedOn = UserSession.loggedIn === true;
      check_if_completed();
    }
  });
}
function check_if_completed() {
  if (userLoggedOn == false) return false;
  var i, reqDataStr = "t=" + storageBlip;  
  MyLearning.makePostRequest(
    MyLearning.getUrl('api.exercise.get'),
    reqDataStr,
    function(reqRes) {
      if (
        reqRes.error.code === '0'
        && reqRes.status === 200
        && reqRes.dataStr !== 'NOTLOGGEDIN'
      ) {
        var i, x, retval, cc;
        userLoggedOn = true;
        retval = JSON.parse(reqRes.dataStr);
        console.log(retval);
        for (x in retval) {
          if (x.indexOf("w3exerciseanswers_" + storageBlip) > -1) {
            exnames.push(x)
          }
        }
        for (i = 0; i < exnames.length; i++) {
          completed = true;
          if (retval[exnames[i]].indexOf("0") > -1) {
            completed = false;
          }
          if (completed) {
            cc = exnames[i];
            cc = cc.replace("w3exerciseanswers_" + storageBlip, "");
            console.log(cc);
            if (document.getElementById("xr_" + cc)) {
              document.getElementById("xr_" + cc).classList.add("completed");
            }
          }
        }
      }
    }
  );
}
