fruits.set("oranges", 200);

// List all entries
let text = "";
fruits.forEach (function(value, key) {
  text += key + ' = ' + value;
})

// List all entries
let text = "";
for (const x of fruits.entries()) {
  text += x;
}

// List all keys
let text = "";
for (const x of fruits.keys()) {
  text += x;
}

// List all values
let text = "";
for (const x of fruits.values()) {
  text += x;
}

// Sum all values
let total = 0;
for (const x of fruits.values()) {
  total += x;
}

