function myArrayMin(arr) {  let len = arr.length;  let min = Infinity;  while (len--) {    
  if (arr[len] < min) {      
  min = arr[len];    }  }  return min;}

function myArrayMax(arr) {
  let len = arr.length;
  let max = -Infinity;
  while (len--) {
        
  if (arr[len] > max) {      
  max = arr[len];    }  }  return max;}

